import React, { useState, useEffect} from 'react';
import Inputvalidator from './Utils/inputValidator'


function App() {

const intialFormValues = {
  Seats : {
    value : "",
    error : ""
  },
  Passengers : {
    value : "",
    error : ""
  }
}

const [formValues, setFormValues] = useState(intialFormValues);


const HandlingOnchangeValue = (e) => {
  const { name, value } = e.target;
  setFormValues(formValues => ({
    ...formValues,
    [name]: {
      ...formValues[name],
      value : value
    }
  }));
}

function submitHandling (formValues) {
  // setFormValues(Inputvalidator(formValues))
  console.log("Submit DATA : ",formValues)
}
// useEffect(() => {
//   // action on update of formValues
//   setFormValues(Inputvalidator(formValues))
//   console.log("AFTER VALIDATION :", formValues)

// }, [formValues]);

// console.log(">> State ",formValues)

  return (
    <div className='w-full ml-sm'>
      <div className='flex items-center justify-center'>
        <div className="m-10 p-12 bg-white broder rounded-lg drop-shadow-lg font-mono text-gray-500">
          {/* ? Title */}
          <p className="text-xl text-gray-500 p-3 border-b border-color-500"> Airplane	seating	algorithm </p>

         {/* ? Rules */}
          <div className="mt-4">
            <p className='text-l font-semibold text-red-500'>Rules : </p>
            <p className='text-sm'>   <br/> Col = must be below 50 <br/>Row =  must be below 5</p>
          </div>

          {/* ? Form */}
        <form>
          <div className='mt-4'>
            <p className='mt-4 text-l font-semibold text-green-500'>Input : </p>
            <div className='mt-4'>
              <p className='text-sm'>Seats [row, col] </p>
              <input
                type="input"
                name="Seats"
                className={`mt-1 w-full px-3 py-2 bg-white border shadow-sm rounded-md ${!formValues.Seats.error ? 'border-slate-300' : 'border-red-500'}`}
                placeholder="[3,2],[4,2]"
                onChange={(e) => (HandlingOnchangeValue(e))}
              ></input>
                  {formValues.Seats.error ? <p className="mt-2  text-red-500 text-sm">
                  {formValues.Seats.error}
                 </p> : ""}
            </div>
            <div className='mt-3'>
              <p className='text-sm'>Number of Passengers </p>
              <input 
                type="number"
                name="Passengers"
                min="0"
                className='mt-1 w-full px-3 py-2 bg-white border shadow-sm rounded-md  border-slate-300 focus:border-gray-600'
                placeholder="5"
                onChange={(e) => (HandlingOnchangeValue(e))}
                ></input>

            </div>
            <button
              type='submit'
              onClick={submitHandling(formValues)}
              className='mt-5 bg-sky-500 hover:bg-sky-700 px-5 py-2.5 text-sm rounded-md font-semibold text-white'
            > 
              Create Airplane
            </button>
          </div>
        </form>
        </div>
    </div>
    </div>
  ); 
}

export default App;
