// [row, col] [x, y] [2,3]

function getNextRow(arr, i)
{
    for (i < arr.length; i++;){ 
        if ((i % 2))  return arr[i]} // row
    return ""
}

function getNextCol(arr, i)
{
    for (i < arr.length; i++;){ 
        if (!(i % 2)) return arr[i]} // col
    return ""
}

function beZeroArr(arr, zeros)   
{
    for (let i = 0; i < zeros; i++)   
        arr[i] = 0
    return arr;
}

function beZeroArr2D(arr, x, y)   
{
    for (let i = 0; i < x; i++)   
            arr[i] = beZeroArr(arr[i], y)
    return arr;
}

function beZeroArr3D(Seats) {
    let regex = /\d+/g; // get only numbers
    let seatsArr = Seats.match(regex);

    let arr = [[[]]] // 3D arr
    console.log("Seate :", seatsArr?.length)
    for (let i = 0; i < (seatsArr?.length / 2); i++){   // (Seats / 2) : (int) means how much seats we have 
        let x = ((!i) ? 0 : seatsArr[(i * 2) - 1])
        let y = Number(x) + 1
        // console.log(" (i) in the while : ", i)
        // console.log(" X - ROW in the while of SeatArr: ", seatsArr[x], "Index :", x)
        // console.log(" Y - Col in the while of SeatArr: ", seatsArr[(Number(x) + 1)], "Index :", (Number(x) + 1))
        arr[i] = beZeroArr2D(arr[i], x, y)
    }

return arr;
}


function CreateMap(FormValues) {
    console.log("DATA CREATE MAP:", FormValues)
    beZeroArr3D(FormValues.Seats)
    return ""
}

export default CreateMap
